 <div>
                <h3>{{ article.title }}</h3>
                <p>{{ article.content }}</p>
                <div>{{ article.cover }}</div>
                <p>Crée le {{ article.createdAt ? article.createdAt|date('d/m/Y H:i:s') : '' }}</p>
                <p>Modifié le {{ article.updated ? article.updated|date('d/m/Y H:i:s') : '' }}</p>
                <p>Catégorie : {{ article.category ? article.category.name : 'Non classé' }} </p>
                </div>

                <h4>Commentaires</h4>
                <div>
                {% for comment in article.comments %}
                    <p>{{ comment.username ~ ' - ' ~ comment.createdAt|date('d/m/Y H:i:s')  }}</p>
                    <p>{{ comment.content }}</p>
                {% endfor %}
                </div>

    {{ include('front/comment/_form.html.twig') }}

        <a class=\"btn btn-dark\" href=\"{{ path('home')}}\">Retour</a>    
