    public function getSourceContext()
    {
        return new Source("{% extends 'admin/layout.html.twig' %}

{% block title %}Articles{% endblock %}

{% block content %}
    <h1>Articles</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Title</th>
                <th>Content</th>
                <th>Cover</th>
                <th>Published</th>
                <th>Created_at</th>
                <th>Updated</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for article in articles %}
            <tr>
                <td>{{ article.id }}</td>
                <td>{{ article.title }}</td>
                <td>{{ article.content }}</td>
                <td>{{ article.cover }}</td>
                <td>{% if article.published %}
                <i class= \"fa fa-check\" style =\"color: green\"></i>
                    {% else %}
                <i class= \"fa fa-times\" style =\"color: red\"></i>
                    {% endif %}</td>
                <td>{{ article.createdAt ? article.createdAt|date('Y-m-d H:i:s') : '' }}</td>
                <td>{{ article.updated ? article.updated|date('Y-m-d H:i:s') : '' }}</td>
                <td>
                    <a href=\"{{ path('article_show', {'id': article.id}) }}\"><i class=\"far fa-eye\"></i></a>
                    <a href=\"{{ path('article_edit', {'id': article.id}) }}\"><i class=\"far fa-edit\"></i></a>
                    <a href=\"{{ path('article_change_status', {'id': article.id}) }}\">
                    {% if not article.published %}
                        <i class= \"fa fa-check\" style =\"color: green\">Publier</i>
                    {% else %} 
                        <i class= \"fa fa-times\" style =\"color: red\">Dépublier</i>
                    {% endif %}       
                    </a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"8\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a class=\"btn btn-dark\" href=\"{{ path('article_new') }}\">Create new</a>
{% endblock %}
", "admin/article/index.html.twig", "/opt/lampp/htdocs/blogSymfony/templates/admin/article/index.html.twig");
    }
